import React, { FC } from 'react';
// import { PortfolioContext, IContext } from '../../../Context';

const Education: FC = () => {

  const studies = [
    {
      school: "Latin American Autonomous University",
      title: "Industrial Engineering",
      description: [
        "Industrial Engineer with a minor in Logistics.",
        "Active member of Ingeco, research group, participating in national and international meetings in Medellín, Cartagena and Bucaramanga, on the impact of engineering on national development."
      ],
      status: "Class of 2015",
      image: "https://firebasestorage.googleapis.com/v0/b/leaflet-react.appspot.com/o/unaulalogo.png?alt=media&token=d243c797-c329-4c84-afd0-1f0c03ddee8c"
    },
    {
      school: "Code The Dream",
      title: "Computer Software Engineering",
      description: [
        "I passed the bootcamp test for Code The Dream and I was admitted into the advanced High Noon program where we learn Full-Stack development."
      ],
      status: "In progress",
      image: "https://firebasestorage.googleapis.com/v0/b/leaflet-react.appspot.com/o/codethedreamlogo.webp?alt=media&token=37cdc3c4-9565-4f28-8586-88f93394666a"
    },
    {
      school: "Code Tenderloin",
      title: "Computer Software Engineering",
      description: ["Completed Code Tenderloin’s Job Readiness Program, Code Ramp program and the advanced Code Ramp ++ programs."],
      status: "Class of 2020",
      image: "https://firebasestorage.googleapis.com/v0/b/leaflet-react.appspot.com/o/codetenderloinlogo.png?alt=media&token=9c8304da-29dd-4d6e-a85e-8f157dd9ccc8"
    },
  ];

  // const { studies } = useContext<IContext>(PortfolioContext);
  return (
    <section className="mw7 mt4 center">
      {/* {console.log(studies)} */}
      {
        studies.map((st) => (
          <article key={ st.school } className="pv4 bb b--black-10 ph3 ph0-l">
            <div className="flex flex-column flex-row-ns">
              <div className="w-100 w-60-ns pr3-ns order-2 order-1-ns">
                <h1 className="f3 athelas mt0 lh-title">{st.school}</h1>
                <h3 className="athelas">{st.title}</h3>
                {
                  st.description.map((desc: string, i: number) => (
                    <p key={ `${desc}${i}` } className="tj f5 f4-l lh-copy athelas">
                      {desc}
                    </p>
                  ))
                }
              </div>
              <div className="pl3-ns order-1 order-2-ns mb4 mb0-ns w-100 w-40-ns">
                <img src={ st.image } className="db" alt="" />
              </div>
            </div>
            <p className="f6 lh-copy gray mv0"><span className="ttu">{st.status}</span></p>
          </article>      
        ))
      }
    </section>


  );
}

export default Education;
