import React, { createContext, useEffect } from 'react';

export const PortfolioContext = createContext<any>({});

type Studies = {
  title: string,
  school: string,
  description: string,
  status: string;
}

let studies: Array<Studies>;

export const Provider: React.FC = (props: any) => {

  const apiKey: string = "keyBeXZyPUF8I8G0u"

  useEffect(() => {
    fetch(`https://api.airtable.com/v0/appkh2au2vrFCqrR6/Table%201?api_key=${apiKey}`)
    .then(response => response.json())
    .then(data => {
      studies = (data.records.map((study: any) => study.fields));
    })
  }, []);


  return (
    <PortfolioContext.Provider value={{
      studies
    }}>
      { props.children }
    </PortfolioContext.Provider>
  );
}
