import React, { FC/* , useContext */ } from 'react';
// import { PortfolioContext, IContext } from '../../../Context';

const Experience: FC = () => {
  // const { experience } = useContext<IContext>(PortfolioContext);

  const experience = [
    {
      title: "Teacher Assistant",
      company: "Code Tenderloin",
      description: [
        "-Code Tenderloin is a non-profit organization that teaches programming and economic empowerment to students with unfavorable backgrounds, in order to provide them with more opportunities and reduce the inequality of work that exists in the field of technology in San Francisco and the bay area.",
        "-I am currently a TA in Code Tenderloin, in the Code Ramp ++ program, where JavaScript is taught. I give programming classes, mock interviews and extra class consultations."
      ],
      status: "April 2020 - Present"
    },
    {
      title: "Teacher Assistant",
      company: "Code the Dream",
      description: [
        "-My role is to code review student projects, provide advice on best practices and how to write quality code, and also show them techniques to debug their code successfully (mostly in HTML, CSS, JavaScript and ReactJS).",
        "-Founder and manager of the group \"Code the Dream Students\", in which students can propose real projects and develop them together with other students. The main purpose of Code the Dream Students is applying all the tools provided by code the dream, encouraging students to work as a team and use version control and teamwork tools like Git and Github."
      ],
      status: ""
    },
    {
      title: "",
      company: "",
      description: "",
      status: ""
    },
    {
      title: "",
      company: "",
      description: "",
      status: ""
    },
    {
      title: "",
      company: "",
      description: "",
      status: ""
    },
  ];

  return (
    <section className="mw7 mt4 center" >
      {
        experience.map((exp: Experience) => (
          <article key={ exp.id } className="pv4 bb b--black-10 ph3 ph0-l">
            <div className="flex flex-column flex-row-ns">
              <div className="w-100 pr3-ns order-2 order-1-ns">
                <h1 className="f3 athelas mt0 lh-title">{exp.title}</h1>
                <h3 className="athelas">{exp.company}</h3>
                {
                  JSON.parse(exp.description).map((desc: string, i: number) => (
                    <p key={ `${desc}${i}` } className="tj f5 f4-l lh-copy athelas">
                      {desc}
                    </p>
                  )) 
                }
              </div>
            </div>
            <p className="f6 lh-copy gray mv0"><span className="ttu">{exp.status}</span></p>
          </article>
        ))  
      }
    </section>
  );
}

export default Experience;
